import React from "react";
import ListUsers from "./ListUsers";

class CreaterList extends React.Component {
  constructor(props){
    super(props)
    this.load()
  }

  state = {
    title: [],
    id: [],
    color: [],
  }


  async load() {
    const listik = [];
    const itemSpace = await this.fetchSpace();
    const itemUser = await this.fetchUser();
    for (let item of itemSpace) {
      if (item.status === 1) {
        listik.push(this.statusOne(item.id, itemUser))
      } else if (item.status === 0) {
        listik.push(this.statusZero(item.id))
      } else { console.log("Ошибка") }
    }

    const title = listik.map(getItem, 1);
    const id = listik.map(getItem, 0);
    const color = listik.map(getItem, 2);
    console.log(title, id, color)
    this.setState({
      title,
      id,
      color,
    })
  }

  getItem = (record, number) => record[number]

  async fetchUser() {
    const response = await fetch('/api/users');
    const { users } = await response.json();
    return users;
  }

  async fetchSpace() {
    const response = await fetch('/api/spaces');
    const { data } = await response.json();
    return data;
  }

  statusOne = (id, users) => {
    for (let user of users) {
      if (id === Number(user.parkingLot)) {
        return [id ,`${id} - ${user.secondName} ${user.firstName} - ${user.carNumber}`, 'red']
      }
    }
  }
  statusZero = (id) => {
    return [id ,`${id} - Свободно`, 'green']
  }



  render() {
    return (
      <ListUsers data={this.state.listik}/>
    )
  }

}

export default CreaterList;
