import React from "react";

class List extends React.Component { 

  async load() {
    const listFull = [];
    const itemSpace = await this.fetchSpace();
    const itemUser = await this.fetchUser();
    for(let item of itemSpace){
      if(item.status === 1){
        listFull.push(this.statusOne(item.id, itemUser))
      } else if (item.status === 0){
        listFull.push(this.statusZero(item.id))
      } else {console.log("Ошибка")}
    }
    return listFull.map((id) => <li>{id}</li>);
  }

  statusOne = (id, users) => {
    for(let user of users) {
      if(id === Number(user.parkingLot)){
        return `${id} - ${user.secondName} ${user.firstName} - ${user.carNumber}`
      }
    }
  }
  statusZero = (id) => {
    return `${id} - Свободно`
  }

  async fetchUser() {
    const response = await fetch('/api/users');
    const {users} = await response.json();
    return users;
  }

  async fetchSpace() {
    const response = await fetch('/api/spaces');
    const {data} = await response.json();
    return data;
  }

  render() {
    return(
      <ul>
        {this.load()}
      </ul>
    )
  }
}

export default List;
