    //   } else { console.log("Ошибка") }
    // }

    return a;
  }

  getData = (prev, curr) => ({
    ...prev,
    [curr.id]: curr,
  });

  async fetchUser() {
    const response = await fetch('/api/users');
    const { users } = await response.json();
    return users;
  }

  async fetchSpace() {
    const response = await fetch('/api/spaces');
    const { data } = await response.json();
    return data;
  }

  statusOne = (id, users) => {
    for (let user of users) {
      if (id === Number(user.parkingLot)) {
        return `${id} - ${user.secondName} ${user.firstName} - ${user.carNumber}`
      }
    }
  }
  statusZero = (id) => {
    return `${id} - Свободно`
  }

  data = this.load();

  render() {
    return (
      <ListUsers />
    )
  }

}

export default CreaterList;
