import React from "react";
import ListUsers from "./ListUsers";

class CreaterList extends React.Component {
  constructor(props){
    super(props)
    this.load()
  }

  state = {
    title: [],
    id: [],
    color: [],
  }


  async load() {
    const listik = [];
    const itemSpace = await this.fetchSpace();
    const itemUser = await this.fetchUser();
    for (let item of itemSpace) {
      if (item.status === 1) {
        listik.push(this.statusOne(item.id, itemUser))
      } else if (item.status === 0) {
        listik.push(this.statusZero(item.id))
      } else { console.log("Ошибка") }
    }

    const title = listik.map(this.getTitle);
    const id = listik.map(this.getID);
    const color = listik.map(this.getColor);
    console.log(title, id, color)
    this.setState({
      title,
      id,
      color,
    })

    console.log(this.state.color)
  }

  getTitle = (record) => record.title
  getID = (record) => record.id
  getColor = (record) => record.color

  async fetchUser() {
    const response = await fetch('/api/users');
    const { users } = await response.json();
    return users;
  }

  async fetchSpace() {
    const response = await fetch('/api/spaces');
    const { data } = await response.json();
    return data;
  }

  statusOne = (id, users) => {
    for (let user of users) {
      if (id === Number(user.parkingLot)) {
        return {'id': id ,
        'title': `${id} - ${user.secondName} ${user.firstName} - ${user.carNumber}`,
        'color': 'red'}
      }
    }
  }
  statusZero = (id) => {
    return {'id': id, 
    'title': `${id} - Свободно`,
    'color': 'green'}
  }



  render() {
    return (
      <ListUsers id={this.state.id} title={this.state.title} color={this.state.color}/>
    )
  }

}

export default CreaterList;
