import axios from 'axios';

const baseUrl = 'https://d5e5-5-136-123-38.ngrok-free.app/';

const getHeaders = () => {
  let header = {
    // 'Content-Type': 'application/json; ',
    // 'Content-Type': 'application/x-www-form-urlencoded',
    // Accept: 'application/json, text/plain, /',
    // 'Access-Control-Allow-Origin':  '*',
  };

  // header.Authorization = Bearer ${USER_TOKEN};
  return header;
};

export const apiPost = async (url, params = {}) => {
  const headers = getHeaders();
  console.log(
    'POST Url: ',
    `${baseUrl}${url}`,
    'Тело запроса: ',
    params,
    'headers: ',
    headers,
  );

  let data = await axios
    .post(`${baseUrl}${url}`, params, {
      ...headers,
      timeout: 60000,
    })
    .then(result => {
      return result.data;
    })
    .catch(err => {
      throw err;
    });

  return data || false;
};

export const apiGet = async (url, params = {}) => {
  const headers = await getHeaders();
  console.log(
    'GET Url: ',
    `${baseUrl}${url}`,
    'Тело запроса: ',
    params,
    'headers: ',
    headers,
  );

  const data = await axios
    .get(`${baseUrl}${url}`, {
      params,
      headers,
      timeout: 60000,
    })
    .then(result => {
      return result.data;
    })
    .catch(err => {
      throw err;
    });

  return data || false;
};
